// import arrow from '../../../asset/svg/arrow.svg';

import Link from "next/link";

class Card extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            link: false,
        }
    }
    render() {
        return (

            <div className="card" style={{ background: `url(${this.props.background})`, backgroundColor: this.props.backcolour, backgroundSize: "cover" }}>
                <h3 className="title">
                    {this.props.title}
                </h3>
                <h3 className="sub-title">
                    {this.props.subTitle}
                </h3>
                <p>
                    {this.props.content}
                </p>
                <p>
                    {this.props.content2}
                </p>
                <Link href="#">
                    <a className="link">
                        {this.props.link}
                    </a>
                </Link>

                <style jsx global>{
                    `.card{
                        box-shadow: 0px 0px 0px 0px;
                        outline: transparent;
                        width: 100%;
                        height: 100%;
                        color: white !important;
                        ${this.props.background!==undefined?`background: linear-gradient(rgba(12, 30, 34, 0.65),${this.props.background}) !important;`:`background: blue !important;`}
                        ${console.log(this.props.background.e)}
                        display: flex;
                        justify-content: center;
                        flex-direction: column;
                        background-position: center !important;
                        background-size: cover !important;
                    }

                    .card h3{
                        font-size: 36px;
                        margin: 0px 0px;
                    }

                    .card    p{
                        // color: white;
                        font-size: 15px;
                    }

                    .card .link{
                        cursor: pointer;
                        width: fit-content;
                        color: #208FFF;
                    }              
                      `
                }

                </style>
            </div>
        )
    }



}

export default Card;
